				memcpy(&ori_etpmsk, &rg_db.vlan[tmpVid].Ext_portmask,sizeof(rtk_portmask_t));

				//Let LAN port become active in WAN's VLAN member port mask
				out_mac_pmask.bits[0]|=ori_pmsk.bits[0];
