			utpmsk.bits[0]=0x1<<RTK_RG_MAC_PORT_CPU;
		ret_fs = _rtk_rg_createGatewayMacEntry(wanintf->gmac.octet,wanintf->egress_vlan_id,utpmsk.bits[0],intfIdx);
