							pPktHdr->egressWlanDevIdx=i;	//20151210LUKE: keep idx for rate limit
							_rtk_rg_splitJumboSendToMasterWifi(pPktHdr,new_skb,wlan_wds_netdev[i-5]);
							if(flooding==0) return i;
